(function(win) {
	win.szqbl = win.szqbl || {};
	//风向判断
	win.szqbl.getWindDirection = function(wd) {
		var wind = Math.floor((wd + 22.5) / 45) + 1;
		if (wind == 1) {
			return "北风";
		} else if (wind == 2) {
			return "东北风";
		} else if (wind == 3) {
			return "东风";
		} else if (wind == 4) {
			return "东南风";
		} else if (wind == 5) {
			return "南风";
		} else if (wind == 6) {
			return "西南风";
		} else if (wind == 7) {
			return "西风";
		} else if (wind == 8) {
			return "西北风";
		} else {
			return "北风";
		}
	}

	//风向风力
	win.szqbl.getWindForce = function(wf) {
		if (wf >= 0 && wf <= 0.3) {
			return 0;
		} else if (wf > 0.23 && wf <= 1.6) {
			return 1;
		} else if (wf > 1.6 && wf <= 3.4) {
			return 2;
		} else if (wf > 3.4 && wf <= 5.5) {
			return 3;
		} else if (wf > 5.5 && wf <= 8.0) {
			return 4;
		} else if (wf > 8.0 && wf <= 10.8) {
			return 5;
		} else if (wf > 10.8 && wf <= 13.9) {
			return 6;
		} else if (wf > 13.9 && wf <= 17.2) {
			return 7;
		} else if (wf > 17.2 && wf <= 20.8) {
			return 8;
		} else if (wf > 20.8 && wf <= 24.5) {
			return 9;
		} else if (wf > 24.5 && wf <= 28.5) {
			return 10;
		} else if (wf > 28.5 && wf <= 32.7) {
			return 11;
		} else if (wf > 32.7 && wf <= 37) {
			return 12;
		} else if (wf > 37 && wf <= 41.5) {
			return 13;
		} else if (wf > 41.5 && wf <= 46.2) {
			return 14;
		} else if (wf > 46.2 && wf <= 51) {
			return 15;
		} else if (wf > 51 && wf <= 56.1) {
			return 16;
		} else if (wf > 56.1 && wf <= 61.2) {
			return 17;
		} else if (wf > 61.2) {
			return '>17';
		}
		return -1;
	}

	/**
	 * 判断是否使用夏季温度标准，每年11月到3月监控低温
	 */
	win.szqbl.isSummer = function() {
		var month = (new Date()).getMonth();
		if (month >= 10 || month < 2) {
			return false;
		}
		return true;
	}
}(window));